                res.send({
                    code:1,
                    data
                });
            }else{
                const data = await ShopModel.findByIdAndUpdate(_id,{...req.body});
                res.send({
                    code:1,
                    data
                });
            }
           
        } catch (error) {
            next(error);
        }
