            if (parentId != undefined) {
                // 分页查询数据
                let query = {pageNumber:parseInt(pageNumber),pageSize:parseInt(pageSize),conditions:{"parentId":parentId}}
                const data = await CategroyModel.findByPaginationHandler(query,{},{});
