            console.log(res);
            if (res.length) {
                resp.send({
                    code: 1,
                    msg: "添加成功"
                })
            } else {
                resp.send({
                    code: 0,
                    msg: "添加失败"
                })
            }
        } else {
            resp.send({
                code: 0,
                msg: "添加失败"
            })
        }
    }
    static async deleteCategroy(req, resp, next) {
        const { id } = req.body
        if (id != undefined) {
            const res = await CategroyModel.deleteOne({ "_id": id });
            if(res.deletedCount){
                resp.send({
                    code: 1,
                    msg:"删除成功"
                })
            }else{
                resp.send({
                    code: 0,
                    msg:"删除失败"
                })
            }
        }
